import React from 'react';

class FeedForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { numberOfPeople: 0, billAmount: 0 };
        // this.state = {};
        this.handleNumberOfPeopleChange = this.handleNumberOfPeopleChange.bind(this);
        this.handleBillAmountChange = this.handleBillAmountChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleNumberOfPeopleChange(event) {
        // this.state.numberOfPeople = event.target.value;
        console.log(event.target);
        this.setState({ [event.target.name]: event.target.value });
    }

    handleBillAmountChange(event) {
        // this.state.billAmount = event.target.value;
        console.log(event.target);
        this.setState({ [event.target.name]: event.target.value });
    }

    handleSubmit(event) {
        let action = this.props.action;
        let method = this.props.method;
        let mode = this.props.mode;
        event.preventDefault();
        async function postData(url = '', data = {}) {
            let request = {
                method: method,
                mode: mode,
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            };
            const response = await fetch(url, request);
            return await response;
        }
        postData(action, { numberOfPeople: event.target.numberOfPeople, billAmount: event.target.billAmount })
            .then((response) => {
                console.log(response);
            });
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Number of people:
                    <input name="numberOfPeople" type="number" value={this.state.numberOfPeople} onChange={this.handleNumberOfPeopleChange} />
                </label>
                <br />
                <label>
                    Bill amount:
                    <input name="billAmount" type="number" value={this.state.billAmount} onChange={this.handleBillAmountChange} />
                </label>
                <br />
                <input type="submit" value="Submit" />
            </form>
        );
    }
}
export default FeedForm;
