import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import SplitResult from "./SplitResult";
import { useAuth0 } from "../react-auth0-spa";


const FeedForm = (props) => {
    // set variables and states
    const [numberOfPeople, setNumberOfPeople] = useState(0);
    const [billAmount, setBillAmount] = useState(0);
    const [username, setUsername] = useState('Nameless xD');
    const [splitResult, setSplitResult] = useState(0);
    const [modalShow, setModalShow] = useState(false);

    const handleSubmit = (event) => {
        event.preventDefault();
        const { loading, user } = useAuth0();
        if (!loading || user) {
            setUsername(user.name);
        }
        let formData = {
            numberOfPeople: Number(numberOfPeople),
            billAmount: Number(billAmount),
            username: username
        };
        getData(formData);
    };
    const getData = (formData) => {
        let url = this.props.action;
        let method = this.props.method;
        let mode = this.props.mode;
        const requestOptions = {
            method: method,
            mode: mode,
            referrerPolicy: 'origin-when-cross-origin',
            headers: {
                'Content-Type': 'application/json; charset=utf-8'
            },
            body: JSON.stringify(formData)
        };
        fetch(url, requestOptions).then(response => response.json()).then(data => {
            setUsername(data.name);
            setSplitResult(data.money);
            setModalShow(true);
        });
    };
    return (
        <Form onSubmit={event => handleSubmit(event)}>
            <Form.Row>
                <Form.Group controlId="feedFormNumberOfPeople">
                    <Form.Label>Number of people</Form.Label>
                    <Form.Control as="select" value={numberOfPeople} name="numberOfPeople" onChange={event => setNumberOfPeople(event.target.value)}>
                        <option>Choose...</option>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                        <option>6</option>
                        <option>7</option>
                        <option>8</option>
                        <option>9</option>
                    </Form.Control>
                    <Form.Text className="text-muted">
                        Please select the quantity of people.
                    </Form.Text>
                </Form.Group>
            </Form.Row>
            <Form.Row>
                <Form.Group controlId="feedFormBillAmount">
                    <Form.Label>Bill amount</Form.Label>
                    <Form.Control name="billAmount" type="number" value={billAmount} onChange={event => setBillAmount(event.target.value)} />
                </Form.Group>
            </Form.Row>
            <Button variant="success" type="submit">
                Submit
            </Button>
            <SplitResult show={this.state.modalShow} onHide={() => this.setModalShow(false)} username={this.state.username} splitResult={this.state.splitResult} />
        </Form>
    );
};


export default FeedForm;
