import React, { Fragment } from "react";
// import { useAuth0 } from "../react-auth0-spa";
import { Table, Form, Button } from 'react-bootstrap';

const BillsHistory = (props) => {
    let tableData = [];
    let _props = {
        action: 'http://localhost:8080/history',
        method: 'POST',
        mode: 'cors'
    }
    const handleSubmit = event => {
        event.preventDefault();
        let formData = {};
        let url = _props.action;
        let method = _props.method;
        let mode = _props.mode;
        const requestOptions = {
            method: method,
            mode: mode,
            referrerPolicy: 'origin-when-cross-origin',
            headers: {
                'Content-Type': 'application/json; charset=utf-8'
            },
            body: JSON.stringify(formData)
        };
        fetch(url, requestOptions).then(response => response.json()).then(data => {
            tableData = isArray(data) && data.length > 0 ? data : [];
            // setSplitResult(!isNaN(data.money) ? data.money : 0);
            // setModalShow(true);
        });
    };
    // const { loading, user } = useAuth0();

    // if (loading || !user) {
    //     return <div>Loading...</div>;
    // }

    if (props.username && props.splitResult) {
        tableData.push({
            key: Number(props.key),
            numberOfPeople: Number(props.numberOfPeople),
            billAmount: !isNaN(Number(props.billAmount)) ? Number(props.billAmount) : 0,
            username: props.username,
            notes: props.notes
        });
    }

    return (
        <Fragment>
            <Form onSubmit={handleSubmit}>
                <Button variant="success" type="submit">Get table data</Button>
            </Form>
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Number of People</th>
                        <th>Bill Amount</th>
                        <th>Username</th>
                        <th>Notes</th>
                    </tr>
                </thead>
                <tbody>
                    {(
                        <tr>
                            <td>{props.key}</td>
                            <td>{props.numberOfPeople}</td>
                            <td>$ {props.billAmount}</td>
                            <td>{props.username}</td>
                            <td>{props.notes}</td>
                        </tr>
                    )}
                    {/* {tableData.length > 0 && tableData.map((elem, index) => {
                        return (
                            <tr>
                                <td>{index + 1}</td>
                                <td>{elem.username}</td>
                                <td>$ {elem.splitResult}</td>
                            </tr>
                        );
                    })} */}
                </tbody>
            </Table>
        </Fragment>
    );
};

export default BillsHistory;
