import React from 'react';
import { Form, Button } from 'react-bootstrap';
import SplitResult from "./SplitResult";

class FeedForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { numberOfPeople: 0, billAmount: 0, username: '', splitResult: 0 };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({ [name]: value });
    }
    handleSubmit(event) {
        event.preventDefault();
        let formData = { numberOfPeople: Number(this.state.numberOfPeople), billAmount: Number(this.state.billAmount) };
        this.getData(formData);
    }
    getData(formData) {
        let url = this.props.action;
        let method = this.props.method;
        let mode = this.props.mode;
        const requestOptions = {
            method: method,
            mode: mode,
            referrerPolicy: 'origin-when-cross-origin',
            headers: {
                'Content-Type': 'application/json; charset=utf-8'
            },
            body: JSON.stringify(formData)
        };
        fetch(url, requestOptions)
            .then(response => response.json())
            .then(data => this.setSplitResult(data));
    }
    setSplitResult(data) {
        this.setState({ ['username']: data.name });
        this.setState({ ['splitResult']: data.money });
        console.log(this.state);
    }
    render() {
        return (
            <Form onSubmit={this.handleSubmit}>
                <Form.Row>
                    <Form.Group controlId="feedFormNumberOfPeople">
                        <Form.Label>Number of people</Form.Label>
                        <Form.Control as="select" value={this.state.numberOfPeople} name="numberOfPeople" onChange={this.handleChange}>
                            <option>Choose...</option>
                            <option>1</option>
                            <option>2</option>
                            <option>3</option>
                            <option>4</option>
                            <option>5</option>
                            <option>6</option>
                            <option>7</option>
                            <option>8</option>
                            <option>9</option>
                        </Form.Control>
                        <Form.Text className="text-muted">
                            Please select the quantity of people.
                        </Form.Text>
                    </Form.Group>
                </Form.Row>
                <Form.Row>
                    <Form.Group controlId="feedFormBillAmount">
                        <Form.Label>Bill amount</Form.Label>
                        <Form.Control name="billAmount" type="number" value={this.state.billAmount} onChange={this.handleChange} />
                    </Form.Group>
                </Form.Row>
                <Button variant="success" type="submit">
                    Submit
                </Button>
                <SplitResult
                    show={true}
                // onHide={() => setModalShow(false)}
                />
            </Form>
        );
    }
}

export default FeedForm;
