import React from 'react';

class FeedForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { peopleValue: '', billAmountValue: '' };
        this.handlePeopleChange = this.handlePeopleChange.bind(this);
        this.handleBillAmountChange = this.handleBillAmountChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handlePeopleChange(event) {
        this.setState({ peopleValue: event.target.value });
    }

    handleBillAmountChange(event) {
        this.setState({ billAmountValue: event.target.value });
    }

    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Number of people:
                    <input type="number" value={this.state.value} onChange={this.handlePeopleChange} />
                </label>
                <br />
                <label>
                    Bill amount:
                    <input type="number" value={this.state.value} onChange={this.handleBillAmountChange} />
                </label>
                <br />
                <input type="submit" value="Submit" />
            </form>
        );
    }
}
export default FeedForm;
