import React from 'react';
import { Modal, Button } from 'react-bootstrap';

class SplitResult extends React.Component {
    constructor(props) {
        super(props);
        this.state = { username: '', splitResult: 0 };
        this.handleChange = this.handleChange.bind(this);
    }
    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({ [name]: value });
    }
    setModalShow(value) {
        this.setState({ ['show']: value });
    }
    render() {
        return (
            <Modal size="lg" aria-labelledby="contained-modal-title-vcenter" centered>
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Result for {this.state.username}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <h5>Bill Split</h5>
                    <p>
                        {this.state.splitResult}
                    </p>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.setModalShow(false)}>Close</Button>
                </Modal.Footer>
            </Modal>
        );
    }
}

export default SplitResult;
