import React from 'react';

class FeedForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { numberOfPeople: '', billAmount: '' };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    // handleChange(event) {
    //     this.setState({ numberOfPeople: event.target.numberOfPeople, billAmount: event.target.billAmount });
    //     console.log(this.setState);
    // }

    handleNumberOfPeopleChange(event) {
        this.setState.numberOfPeople = event.target.numberOfPeople;
        console.log(this.setState);
    }

    handleBillAmountChange(event) {
        this.setState({ numberOfPeople: event.target.numberOfPeople, billAmount: event.target.billAmount });
        console.log(this.setState);
    }

    handleSubmit(event) {
        let action = this.props.action;
        let method = this.props.method;
        let mode = this.props.mode;
        event.preventDefault();
        async function postData(url = '', data = {}) {
            let request = {
                method: method,
                mode: mode,
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            };
            const response = await fetch(url, request);
            return await response;
        }
        postData(action, { numberOfPeople: event.target.numberOfPeople, billAmount: event.target.billAmount })
            .then((response) => {
                console.log(response);
            });
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Number of people:
                    <input type="number" value={this.state.numberOfPeople} onChange={this.handleNumberOfPeopleChange} />
                </label>
                <br />
                <label>
                    Bill amount:
                    <input type="number" value={this.state.billAmount} onChange={this.handleBillAmountChange} />
                </label>
                <br />
                <input type="submit" value="Submit" />
            </form>
        );
    }
}
export default FeedForm;
